class Solution {
public:
    string largestPalindromic(string num) {
        map<char, int> mp;
        for(auto i : num)mp[i]++;
        vector<pair<char, int>> arr;
        for(auto i : mp){
            arr.push_back(i);
        }
        reverse(arr.begin(), arr.end());
        string t1, t2, t3;
        for(int i = 0; i < arr.size(); i++){
            if(arr[i].second%2 && t3 == ""){
                t3.push_back(arr[i].first);
            }
            for(int j= 0; j < (arr[i].second - arr[i].second%2)/2; j++){
                t1.push_back(arr[i].first);
            }
        }
        if(t1.size() > 0 && t1[0] == '0')t1 = "";
        t2 = t1;
        reverse(t2.begin(), t2.end());
        string ans = t1 + t3 + t2;
        if(ans == "")return "0";
        return ans;
    }
};
