class Solution {
public:
    int orderOfLargestPlusSign(int n, vector<vector<int>>& mines) {
        if(n==1) return 0;
        vector<vector<int>> vec(n,vector<int>(n,1)),r(n,vector<int> (n,0)),l(n,vector<int> (n,0)),u(n,vector<int> (n,0)),d(n,vector<int> (n,0));
        for(auto iter:mines){
            vec[iter[0]][iter[1]]=0;
        }
        for(int i=0;i<n;i++){
            int temp=vec[i][0];
            r[i][0]=temp;
            // cout<<temp<< " ";
            for(int j=1;j<n;j++){
                if(vec[i][j]==0) temp=0;
                r[i][j]=temp+vec[i][j];
                // cout<<r[i][j]<<" ";
                temp=temp+vec[i][j];
            }
            // cout<<endl;
        }
        // cout<<endl;
        for(int i=0;i<n;i++){
            int temp=vec[i][n-1];
            l[i][n-1]=temp;
            // cout<<temp<< " ";
            for(int j=n-2;j>=0;j--){
                if(vec[i][j]==0) temp=0;
                l[i][j]=temp+vec[i][j];
                // cout<<l[i][j]<<" ";
                temp=temp+vec[i][j];
            }
            // cout<<endl;
        }
        // cout<<endl;
        for(int i=0;i<n;i++){
            int temp=vec[0][i];
            d[0][i]=temp;
            // cout<<temp<< " ";
            for(int j=1;j<n;j++){
                if(vec[j][i]==0) temp=0;
                d[j][i]=temp+vec[j][i];
                // cout<<d[j][i]<<" ";
                temp=temp+vec[j][i];
            }
            // cout<<endl;
        }
        // cout<<endl;
        for(int i=n-1;i>=0;i--){
            int temp=vec[n-1][i];
            u[n-1][i]=temp;
            // cout<<temp<< " ";
            for(int j=n-2;j>=0;j--){
                if(vec[j][i]==0) temp=0;
                u[j][i]=temp+vec[j][i];
                // cout<<u[j][i]<<" ";
                temp=temp+vec[j][i];
            }
            // cout<<endl;
        }
        // cout<<endl;
        int ans=INT_MIN;
        for(int i=0;i<n;i++){
            for(int j=0;j<n;j++){
                // cout<<min(r[i][j],min(d[i][j],min(u[i][j],l[i][j])))<<" ";
                ans=max(ans,min(r[i][j],min(d[i][j],min(u[i][j],l[i][j]))));
            }
        }
        // cout<<d[2][1]<<" ";
        return ans;

    }
};
