class Solution {
public:
    int findMaxK(vector<int>& nums) {
        map<int, int> mp;
        for(auto i : nums)mp[i]++;
        int ans = -1;
        for(auto i : nums){
            if(mp[-i] > 0){
                ans = max(ans, abs(i));
            }
        }
        return ans;
    }
};
