class Solution {
public:
    bool lemonadeChange(vector<int>& bills) {
        map<int, int> mp;
        for(auto i : bills){
            cout<<i<<" ";
            if(i == 10){
                if(mp[5] > 0)mp[5]--;
                else return false;
            }else if(i == 20){
                if(mp[5] > 0 && mp[10] > 0){
                    mp[5]--, mp[10]--;
                }else if(mp[5] >= 3){
                    mp[5] -= 3;
                }else  return false;
            }
            mp[i]++;
        }
        return true;
    }
};
