class Solution {
public:
    int lengthOfLastWord(string s) {
        stack<string> st;
        string prev = "";
        for(int i=0;i<s.length();i++){
            if(s[i] == ' '){
                st.push(prev);
                prev = "";
            }else{
                prev.push_back(s[i]);
            }
        }
        st.push(prev);
        while(st.top().length() ==  0){
            st.pop();
        }
        return st.top().length();
    }
};
