class Solution {
public:
    int longestContinuousSubstring(string s) {
        int n = s.length();
        vector<int> pre(n, 1);
        for(int i = 1; i < n; i++){
            if(s[i] == s[i - 1] + 1){
                pre[i] = pre[i - 1] + 1;
            }
        }
        return *max_element(pre.begin(), pre.end());
    }
};
