class Solution {
public:
    string smallestString(string s) {
        int ind = 0;
        for(int i = 0; i <  s.length(); i++){
            if(s[i] == 'a'){
                ind = i + 1;
            }else{
                break;
            }
        }
        if(ind == s.length()){
            s.back() = 'z';
            return s;
        }
        for(int i = ind; i < s.length(); i++){
            if(s[i] == 'a')break;
            else s[i]--;
        }
        return s;
    }
};
