class Solution {
public:
    string licenseKeyFormatting(string s, int k) {
        string ans = "";
        int cnt = 0;
        for(int i = s.length() - 1; i >=0;i--){
            if(cnt == k){
                cnt = 0;
                ans.push_back('-');
            }
            if(s[i] == '-'){
                continue;
            }else if(s[i] >='0' && s[i] <='9'){
                ans.push_back(s[i]);
            }else if(s[i] >='a' && s[i] <='z'){
                ans.push_back(s[i] - 'a' + 'A');
            }else if(s[i] >='A' && s[i] <='Z'){
                ans.push_back(s[i]);
            }
            cnt++;
        }
        while(ans.size() > 0 && ans[ans.size() - 1] == '-'){
            ans.pop_back();
        }
        reverse(ans.begin(), ans.end());
        return ans;
    }
};
