/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode(int x) : val(x), next(NULL) {}
 * };
 */
class Solution {
public:
    bool hasCycle(ListNode *head) {
        ListNode *temp = head;
        unordered_map<ListNode *,int> umap;
        while(temp != NULL){
            umap[temp]++;
            if(umap[temp] > 1) return true;
            temp = temp->next;
        }
        return false;

    }
};
