class Solution {
public:
    int alternatingSubarray(vector<int>& arr) {
        int n = arr.size();
        int ans = -1;
        for (int i = 0; i < n; i++)
        {
            int curr = 1;
            for (int j = i; j < n - 1; j++)
            {
                if (curr == 1 && arr[j + 1] - arr[j] == 1)
                {
                    ans = max(ans, j - i + 2);
                }
                else if (curr == 0 && arr[j + 1] - arr[j] == -1)
                {
                    ans = max(ans, j - i + 2);
                }
                else
                {
                    break;
                }
            curr = 1 - curr;
            }
        }
        return ans;
    }
};
