class Solution {
public:
    map<int, int> indx;    

    int longestSubsequence(vector<int>& arr, int difference) {
        map<int, int> dp;
        dp[arr[0]] = 1;
        for(int i = 1; i < arr.size(); i++) {
            dp[arr[i]] = 1 + dp[arr[i] - difference];
        }
        int res = 0;
        for(int i = 0; i < arr.size(); i++) {
            res = max(res, dp[arr[i]]);
        }
        return res;
    }
};
