class Solution {
public:
    int longestConsecutive(vector<int>& nums) {
        if(nums.size() == 0)return 0;
        set<int> st;
        for(auto i : nums){
            st.insert(i);
        }
        int count = 1;
        int prev = *(st.begin());
        st.erase(st.begin());
        int _max = -1e9-1;
        for(auto i : st){
            if(prev + 1 == i){
                count++;
            }else{
                _max = max(_max, count);
                count = 1;
            }
            prev = i;
        }
        _max = max(_max, count);
        return _max;
    }
};
