class Solution {
public:
    int findLengthOfLCIS(vector<int>& nums) {
        int _max = 1;
        int curr = 1;
        for(int i = 1; i < nums.size();i++){
            if(nums[i] > nums[i-1]){
                curr++;
                _max = max(_max, curr);
            }else{
                curr = 1;
            }
        }
        return _max;
    }
};
