class Solution {
public:
    int longestAlternatingSubarray(vector<int>& nums, int threshold) {
        int n = nums.size(), ans = 0;
        for(int i = 0; i <n; i++){
            if(nums[i]%2 == 0 && nums[i] <= threshold){
                ans = max(ans, 1);
                for(int j = i + 1; j < n; j++){
                    if(nums[j]%2 != nums[j-1]%2 && nums[j] <= threshold){
                        ans = max(ans, j - i + 1);
                    }else{
                        break;
                    }
                }
            }
        }
        return ans;
    }
};
