class Solution {
public:
    int longestNiceSubarray(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>> pre(n, vector<int>(32, 0));
        for(int i = 0; i < n; i ++){
            for(int j = 0; j < 32;j ++){
                if((nums[i] & (1<<j)))pre[i][j] = 1;
            }
        }
        int i = 0, j = 0;
        vector<int> temp(32, 0);
        int ans = 0;
        while(j < n){
            int f = 1;
            for(int k = 0; k < 32; k++){
                temp[k] += pre[j][k];
            }
            while(i < j){
                int f = 1;
                for(int k = 0; k < 32;k ++){
                    if(temp[k] > 1){
                        f = 0;
                        break;
                    }
                }
                if(f)break;
                for(int k = 0; k < 32; k++){
                    temp[k] -= pre[i][k];
                }
                i++;
            }
            j++;
            ans = max(ans, j - i);
        }
        return ans;
    }
};
