class Solution {
public:
    int longestPalindrome(string s) {
        unordered_map<char, int> m;
        for(auto i : s){
            m[i]++;
        }
        int ans = 0, o  = 0;
        for(auto i : m){
            ans += i.second;
            if(i.second%2 == 1)
                o++;
        }
        if(o == 0){
            return ans;
        }
        return ans - (o - 1);
    }
};
