class Solution {
public:
    int longestSquareStreak(vector<int>& nums) {
        unordered_map<long long,int> umap;
        for(auto i:nums) umap[i]++;
        int ans=0;
        int mx=0;
        sort(begin(nums),end(nums));
        for(auto i:nums){
            long long t=1*i;
            mx=0;
            while(t<=INT_MAX){
                if(umap.count(t)){
                    mx++;
                }else break;
                t*=t;
            }
            // cout<<i<<" "<<mx<<endl;
            ans=max(mx,ans);
        }
        return ans==1?-1:ans;
    }
};
