class Solution {
public:
    int longestSubarray(vector<int>& nums) {
        int n = nums.size();
        vector<int> left, right(n);
        int ans = 0, curr = 0;

        for(auto i : nums){
            left.push_back(curr);
            if(i == 0)curr = 0;
            else curr++;
        }
        curr = 0;
        for(int i = n - 1; i >=0 ;i--){
            right[i] = curr;
            if(nums[i] == 0)curr = 0;
            else curr++;
        }
        for(int i = 0; i < n; i++){
            ans = max(ans, left[i] + right[i]);
        }
        return ans;
    }
};
