class Solution {
public:
    int longestSubarray(vector<int>& nums) {
        int cnt = 0, prev = nums[0];
        map<int, int> mp;
        for(auto i : nums){
            if(prev == i){
                cnt++;
            }else{
                mp[prev] = max(mp[prev], cnt);
                cnt = 1;
            }
            prev = i;
        }
        mp[prev] = max(mp[prev], cnt);
        return mp.rbegin()->second;
    }
};
