class Solution
{
    bool check(vector<int> &arr)
    {
        for (int i = 0; i < arr.size(); i++)
        {
            if (arr[i] > 1)
            {
                return false;
            }
        }
        return true;
    }

public:
    int lengthOfLongestSubstring(string s)
    {
        int i = 0, j = 0;
        int n = s.length();
        int ans = INT_MIN;
        vector<int> arr(1000, 0);
        while (j < n)
        {
            if (check(arr))
            {
                arr[s[j]]++;
                ans = max(ans, j - i);
                j++;
            }
            else
            {
                arr[s[i]]--;
                i++;
            }
        }
        if (check(arr))
        {
            ans = max(ans, j - i);
        }
        return ans;
    }
};
