class Solution {
public:
    vector<int> luckyNumbers (vector<vector<int>>& v) {
         int n = v[0].size();

    vector<int> ans;
    for (int i = 0; i < v.size(); i++) {

        int index = 0;
        int x = *min_element(v[i].begin(), v[i].end());

        for (int j = 0; j < n; j++) {

            if (v[i][j] != x) {
                index++;
            }

            int maxi = INT_MIN;
            for (int k = 0; k < v.size(); k++) {

                maxi = max(maxi, v[k][index]);

            }

            if (maxi == x) ans.push_back(x);


        }

    }

        if(ans.size() > 1){
            ans.pop_back();
            return ans;
        }

    return ans;
    }
};
