class Solution {
public:
    int maxDistance(vector<int>& position, int m) {
        int lo = 0, hi = 1e9, ans = 0;
        sort(position.begin(), position.end());
        while(lo <= hi){
            int mid = (lo + hi + 1)/2;
            long long prev = -1e18, t = 0;
            for(int i = 0; i < position.size(); i ++){
                if(position[i] - prev >= mid){
                    prev = position[i];
                    t++;
                }
            }
            if(t >= m){
                ans = mid;
                lo = mid + 1;
            }else{
                hi = mid - 1;
            }
        }
        return ans;
    }
};
