class Solution {
public:
    bool canMakeSubsequence(string a, string b) {
        int i = 0, j = 0;
        while(i < a.length() && j < b.length()){
            char c = (a[i] - 'a' + 1)%26 + 'a';
            if(a[i] == b[j] || b[j] == c){
                i++;
                j++;
            }else{
                i++;
            }
        }
        return j == b.length();
    }
};
