class Solution {
public:
    string makeGood(string s) {
        string ans;
        ans.push_back(s[0]);
        for(int i = 1; i < s.size(); i++) {
            if(ans.empty()) {
                ans.push_back(s[i]);
                continue;
            }
            if(s[i] != ans.back()) {
                if(tolower(s[i]) == ans.back() || tolower(ans.back()) == s[i]) {
                    ans.pop_back();
                }
                else {
                    ans.push_back(s[i]);
                }
            }
            else {
                ans.push_back(s[i]);
            }
        }
        return ans;
    }
};
