    // int r,c;
class Solution {
public:

    static bool com(const vector<int> &a, const vector<int> &b){
        return a[2] < b[2];
        // return  abs(a[0]-r) + abs(a[1] -c ) < abs(b[0]-r) + abs(b[1] -c );
    }
    vector<vector<int>> allCellsDistOrder(int R, int C, int r0, int c0) {
      vector<vector<int> > ans;
        vector<vector<int> > arr;
        for(int i=0;i<R;i++){
            for(int j=0;j<C;j++){
                int distance = abs(i-r0) + abs(j-c0);
                arr.push_back({i,j,distance});
            }
        }

        sort(arr.begin(),arr.end(),com);
        for(int i=0;i<arr.size();i++){
            vector<int> v(2);
            v[0] = arr[i][0];            
            v[1] = arr[i][1];

            ans.push_back(v);

        }
        return ans;
    }
};
