class Solution {
public:
    int maxChunksToSorted(vector<int>& arr) {
        int n= arr.size();
        vector<int>nums(n+1);
        nums[n] = INT_MAX;
        for(int i= n-1;i>=0;i--){
            nums[i] = min(nums[i+1] , arr[i]);
        }

        int lmax = 0;
        int chunk = 0;
        for(int i = 0;i<n;i++){
            lmax = max(lmax , arr[i]);
            if(lmax<=nums[i+1]){
                chunk++;
            }
        }
        return chunk;
    }
};
