class Solution {
public:
    int longestOnes(vector<int>& nums, int k) {
        int ans = 0;
        int i = 0, j = 0;
        int curr = 0;
        while(j < nums.size()){
            if(nums[j] == 0)curr++;

            while(curr > k){
                if(nums[i++] == 0)
                    curr--;
            }
            ans = max(ans, j - i + 1);
            j++;
        }
        return ans;
    }
};
