class Solution {
public:
    int maxSum(vector<int>& nums) {
        int n = nums.size();
        int ans = -1;
        for(int i = 0; i  <n; i++){
            string a = to_string(nums[i]);
            int ma = *max_element(a.begin(), a.end());
            for(int j = i + 1; j < n; j++){
                string b = to_string(nums[j]);
                int mb = *max_element(b.begin(), b.end());
                if(mb == ma){
                    ans = max(ans, nums[i] + nums[j]);
                }
            }
        }
        return ans;
    }
};
