class Solution {
public:
    int helper(vector<vector<int>> &points, int ind, vector<int> &prev, int a, int b){
        if(ind == points.size())return 0;
        int take = 0, notTake = 0;
        long long A = prev[0] - points[ind][0];
        long long B = prev[1] - points[ind][1];
        long long _gcd =__gcd(A, B);
        A /= _gcd;
        B /= _gcd;
        if(a == 5 && b == 3){
            take = helper(points, ind + 1, prev, A, B) + 1;
        }else if(a == A && b == B){
            take = helper(points, ind + 1, prev, A, B) + 1;
        }
        notTake = helper(points, ind + 1, prev, a, b);
        return max(take, notTake);
    }
    int maxPoints(vector<vector<int>>& points) {
        int ans = 0;
        for(int i = 0; i < points.size();i++){
            ans = max(ans, 1 + helper(points, i + 1, points[i], 5, 3));
        }
        return ans;        
    }
};
