class Solution {
public:
    int maximalNetworkRank(int n, vector<vector<int>>& roads) {
        vector<int> deg(n, 0);
        unordered_map<int, unordered_map<int, bool> > vis;
        for(auto road : roads) {
            deg[road[0]]++;
            deg[road[1]]++;
            vis[road[0]][road[1]] = true;
            vis[road[1]][road[0]] = true;
        }
        int ans = 0;
        for(int i = 0; i < n; i++) {
            for(int j = i + 1; j < n; j++) {
                ans = max(ans, deg[i] + deg[j] - vis[i][j]);
            }
        }

        return ans;
    }
};
