class Solution {
public:
    long long maxKelements(vector<int>& nums, int k) {
        priority_queue<int> pq;
        long long ans=0;
        for(auto i:nums) pq.push(i);
        while(pq.size()&&k--){
            auto kk=pq.top();
            // cout<<kk<<" ";
            ans+=(long long)kk;
            pq.pop();
            kk=ceil(kk/3.0);
            if(kk) pq.push(kk);
        }
        return ans;
    }
};
