class Solution {
public:
    int maximalSquare(vector<vector<char>>& matrix) {
        int n = matrix.size(), m = matrix[0].size();
        vector<vector<int>> arr(n, vector<int>(m, 0));
        for(int i = 0; i < n;i++){
            for(int j = 0; j < m; j ++){
                arr[i][j] = matrix[i][j] - '0';
            }
        }
        for(int i = 1; i < n;i++){
            for(int j = 1; j < m; j ++){
                if(matrix[i][j] == '1'){
                    arr[i][j] = min(arr[i-1][j], min(arr[i][j-1], arr[i-1][j-1])) + 1;
                }
            }
        }
        int ans = 0;
        for(int i = 0; i < n;i++){
            for(int j = 0; j < m; j ++){
                ans = max(ans, arr[i][j]);
            }
        }
        return ans * ans;
    }
};
