class Solution {
public:
    int maxDistToClosest(vector<int>& s) {
        int n=s.size();
        vector<int> vecr(n),vecl(n);
        int l=INT_MAX;
        for(int i=0;i<n;i++){
            if(s[i]==1) l=i;
            vecl[i]=l;
        }
        int r=INT_MAX;
        for(int i=n-1;i>=0;i--){
            if(s[i]==1) r=i;
            vecr[i]=r;
        }
        int ans=INT_MIN;
        for(int i=0;i<n;i++){
            if(s[i]!=1){
                int k=INT_MAX;
                k=min(k,(vecl[i]==INT_MAX)?INT_MAX:i-vecl[i]);
                k=min(k,(vecr[i]==INT_MAX)?INT_MAX:vecr[i]-i);
                ans=max(ans,k);
            }
        }
        return ans;

    }
};
