class Solution {
public:
    int maximizeGreatness(vector<int>& nums) {
        map<int, int> m;
        for(auto i : nums)m[i]++;
        vector<int> arr;
        for(auto i : m)arr.push_back(i.first);
        reverse(arr.begin(), arr.end());
        int ans = 0;
        int unused = 0;
        for(int i = 0; i < arr.size() - 1;i++){
            unused += m[arr[i]];
            int _min = min(unused, m[arr[i + 1]]);
            ans += _min;
            unused -= _min;
        }
        return ans;
    }
};
