class Solution {
public:
    long long maximumSubsequenceCount(string text, string p) {
        string s;
        s.push_back(p[0]);
        string k;
        for(auto i:text){
            k.push_back(i);
            s.push_back(i);
        }
        k.push_back(p[1]);
        long long ans1=0,ans2=0;

        long long cnt=0;
        for(int i=s.length()-1;i>=0;i--){
            if(s[i]==p[0]){
                ans1+=cnt;
            } if(s[i]==p[1]) cnt++;
        }
        cnt=0;
        for(int i=k.length()-1;i>=0;i--){
            if(k[i]==p[0]){
                ans2+=cnt;
            } if(k[i]==p[1]) cnt++;
        }

        return max(ans1,ans2);

    }
};
