class Solution {
public:
    int kadane(vector<int> &nums){
        int ans = 0, curr =0;
        for(auto i : nums){
            curr += i;
            if(curr < 0)curr = 0;
            ans = max(ans, curr);
        }
        return ans;
    }
    int maxAbsoluteSum(vector<int>& nums) {
        int a = kadane(nums);
        for(auto &i : nums){
            i = -i;
        }
        int b = kadane(nums);
        return max(a, b);
    }
};
