class Solution {
public:
    int maxArea(int h, int w, vector<int>& horizontalCuts, vector<int>& verticalCuts) {
        sort(horizontalCuts.begin(),horizontalCuts.end());
        sort(verticalCuts.begin(),verticalCuts.end());

        int maxheight= max(horizontalCuts[0]-0,h-horizontalCuts[horizontalCuts.size()-1]);
        for(int i =1;i<horizontalCuts.size();i++){
            int maxi = horizontalCuts[i]-horizontalCuts[i-1];
            maxheight = max(maxheight,maxi);


        }

        int maxwidth = max(verticalCuts[0]-0,w-verticalCuts[verticalCuts.size()-1]);
        for(int i =1;i<verticalCuts.size();i++){
            int maxw = verticalCuts[i]-verticalCuts[i-1];
            maxwidth = max(maxwidth,maxw);
        }
       // long long ans = 
       return (1LL*maxheight*maxwidth) % 1000000007;
    }
};
