class Solution {
public:
    double findMaxAverage(vector<int>& nums, int k) {
        long sum = accumulate(nums.begin(), nums.begin() + k, 0);
        double _max = (1.0 * sum)/k;
        int i = 0, j = k - 1;
        while(j < nums.size() - 1){
            j++;
            sum += nums[j];
            sum -= nums[i];
            i++;
            double avg = (1.0 * sum)/k;
            _max = max(_max, avg);
        }   
        return _max;
    }
};
