class Solution {
public:
    int maximumBags(vector<int>& capacity, vector<int>& rocks, int additionalRocks) {

        int n = capacity.size();
        for(int i = 0; i < n; i++) {
            rocks[i] = capacity[i] - rocks[i];
        }

        sort(rocks.begin(), rocks.end());
        int ans = 0;
        for(auto i : rocks) {
            if(additionalRocks >= i) {
                additionalRocks -= i;
                ans++;
            }
        }

    return ans;

    }
};
