class Solution {
public:
    int maximumBeauty(vector<int>& nums, int k) {
        long long int i = 0, ans = 0;
        sort(nums.begin(), nums.end());
        long long int n = nums.size(), sum = 0;
        for(long long int j = 0; j < n; j++){
            sum += nums[j];
            while(nums[j] - nums[i] > 2 * k){
                sum -= nums[i];
                i++;
            }
            ans = max(ans, j - i + 1);
        }
        return ans;
    }
};
