class Solution {
public:

    bool ispossible(vector<int> &candies, long long k, long long mid) {
        long long  cnt = 0;
        for(auto i : candies) {
                cnt += (i / mid);
        }
        return cnt >= k;

    }
    int maximumCandies(vector<int>& candies, long long k) {
        long long s = 1;
        long long e = 1000000000000;
        long long ans = 0;

        while(s <= e) {
            long long mid = (s + e) / 2;

            if(ispossible(candies, k, mid)) {
                ans = max(ans, mid);
                s = mid + 1;
            }
            else {
                e = mid - 1;
            }

        }
        return ans;
    }
};
