class Solution {
public:
    int maximumCount(vector<int>& nums) {
        int ind1 = lower_bound(nums.begin(), nums.end(), 0) - nums.begin() - 1;
        int ind2 = upper_bound(nums.begin(), nums.end(), 0) - nums.begin();
        return max((int)nums.size() - 1 - ind2 + 1, ind1 + 1);
    }
};
