class Solution {
public:
    int maximumDifference(vector<int>& nums) {
        int maxDiff = INT_MIN;
        int n = nums.size();
        int mini = nums[0];

        for(int i = 1; i<n;i++){
            mini = min(mini , nums[i]);
            maxDiff = max(maxDiff , nums[i] - mini);
        }

        return maxDiff == 0 ? -1 : maxDiff;
    }
};
