class Solution {
public:
    int maxDistance(vector<int>& nums1, vector<int>& nums2) {
        int ans = 0;
        for(int i = 0; i < nums1.size();i++){
            int ind = lower_bound(nums2.rbegin(), nums2.rend(), nums1[i]) - nums2.rbegin() ;
            ind = nums2.size() - ind - 1;
            if(ind < i)continue;
            ans = max(ans, ind - i);
        }
        return ans;
    }
};
