class Solution {
public:
    int captureForts(vector<int>& f) {
        int ans=0;
        for(int i=0;i<f.size();i++){
            if(f[i]==1){
                int j=i-1;
                int cnt=0;
                while(j>=0){
                    if(f[j]==0){
                        cnt++;
                    }else if(f[j]==-1){ ans=max(ans,cnt);break;}
                    else break;
                    j--;
                }
                j=i+1;
                cnt=0;
                while(j<f.size()){
                    if(f[j]==0){
                        cnt++;
                    }else if(f[j]==-1) {ans=max(ans,cnt);break;}
                    else break;
                    j++;
                }
            }
        }
        return ans;
    }
};
