class Solution {
public:
    int maximumUniqueSubarray(vector<int>& nums) {
        int ans = 0;
        map<int, int> vis;
        int i = 0;
        int j = 0;
        int sum = 0;

        while(i < nums.size() and j < nums.size()) {
            sum += nums[j];
            vis[nums[j]]++;

            while(i <= j and vis[nums[j]] > 1) {
                sum -= nums[i];
                vis[nums[i]]--;
                i++;
            }
            ans = max(ans, sum);
            j++;
        }
        return ans;
    }
};
