int dp[1010][2100];
class Solution {
public:
    int helper(vector<vector<int>> &pairs, int ind, int prev){
        if(ind == pairs.size()){
            return 0;
        }
        if(dp[ind][prev + 1005] != -1)return dp[ind][prev + 1005];
        int notTake = helper(pairs, ind + 1, prev);
        int take = 0;
        if(prev < pairs[ind][0]){
            take = 1 + helper(pairs, ind + 1, pairs[ind][1]);
        }
        return dp[ind][prev + 1005] = max(take, notTake);
    }
    int findLongestChain(vector<vector<int>>& pairs) {
        memset(dp, -1, sizeof dp);
        sort(pairs.begin(), pairs.end());
        return helper(pairs, 0, -1005);
    }
};
