class Solution {
public:
    int maxNumberOfBalloons(string text) {

        unordered_map<char, int> m;
        for(auto i : text) {
            m[i]++;

        }

        // string s = "balloon"

        // b a l o n

        int cnt = INT_MAX;
        cnt = min(cnt, m['b']);
        cnt = min(cnt, m['a']);
        cnt = min(cnt, (m['l'])/2);
        cnt = min(cnt, (m['o'])/2);
        cnt = min(cnt, m['n']);
        return cnt;


    }
};
