class Solution {
public:
    int countBalls(int lowLimit, int highLimit) {
            vector<int> v(50);
            for(int i = lowLimit;i <= highLimit ;i++){
                int x = i;
                int sum = 0;
                while(x){
                    sum += x%10;
                    x/=10;  
                }
               v[sum]++;
            }



        int p = 0;
        for(auto node:v){
            if(node > p){
                p = node;
            }
        }
        return p;
    }
};
