class Solution {
public:
    bool check(string &s, string &p){
        int i = 0, j = 0;
        while(i < s.length() && j < p.length()){
            if(s[i] != '*' && s[i] == p[j])
                j++;
            i++;
        }
        return j == p.length();
    }
    int maximumRemovals(string s, string p, vector<int>& arr) {
        long lo = 0, hi = arr.size();
        long ans = 0;
        string org = s;
        while(lo <= hi){
            long mid = (lo + hi + 1)/2;
            int i = 0, j = 0;
            for(int i = 0; i < mid; i++){
                s[arr[i]] = '*';
            }
            if(check(s, p)){
                ans = mid;
                lo = mid + 1;
            }else{
                for(int i = 0; i < mid; i++){
                    s[arr[i]] = org[arr[i]];
                }
                hi = mid - 1;
            }
        }
        return ans;
    }
};
